    var _this3 = this;

    var el = this.$refs.dragTable.$el.querySelectorAll(".el-table__body-wrapper > table > tbody")[0];
    var sortable = Sortable.create(el, {
      handle: ".allowDrag",
      onEnd: function onEnd(evt) {
        var targetRow = _this3.cloumns.splice(evt.oldIndex, 1)[0];

        _this3.cloumns.splice(evt.newIndex, 0, targetRow);

        for (var index in _this3.cloumns) {
          _this3.cloumns[index].sort = parseInt(index) + 1;
        }
      }
    });
  }
};
